clc; close all; clear;

% Inputs
Pc = 200:10:500; % psi
Pa = 12.7;
OF = 2.35;

A_e = (pi*6.5^2/4)*0.0254^2; % m^2 (do not change)
T = 5000/3 * 4.448; % N

C_star_eff = 0.9;
C_F_eff = 0.95;

D_e = 4.1;
A_e = (pi*D_e.^2/4)*0.0254^2;
% Setup
for i=1:length(Pc)
    mdot_old = 0;
    if i ==1
    mdot(i) = 1;
    else
    mdot(i) = mdot(i-1);
    end
    A_t = 1e-3;
    
    tol = 1e-5;
    err = 1;
    while abs(err)>tol
    
        data = CEA('problem','rocket','equilibrium','fac','ma,kg/s',mdot(i),'o/f',OF,'p(psi)',Pc(i)+Pa,'supsonic(ae/at)',A_e/A_t,'reactants','fuel','RP-1','wt%',100,'t(k)',298.15,'oxid','O2(L)','wt%',100,'t(k)',90.0,'output','transport','mks','end');
        C_star = data.output.eql.cstar(1);
        C_F = data.output.eql.cf(end);
        isp(i) = data.output.eql.isp(end);
        mdot(i) = T/(C_star*C_star_eff*C_F*C_F_eff);
        A_t = C_star*C_star_eff*mdot(i)/((Pc(i)+Pa)*6894.7);
        err = mdot_old-mdot(i);
        mdot_old = mdot(i);
        if A_e<A_t
            mdot(i) = NaN;
            break
        end
    end
    clc
    fprintf('%.0f%%\n',i/length(Pc)*100)
end

